mapper.update:
  		update member
		set h_school_code = '', h_school_cate='', u_school_code = '', u_school_cate='' 
		where user_id=#{id}

mapper.select:
select count(*) 
from mentor 
where user_id=#{id}

select school_name 
from h_school 
where school_code=#{hCode}

select name 
from u_school 
where code=#{uCode}

select cate 
from mentor 
where user_id=#{id}

select count(*)
from member
where user_id = #{id}

mapper.update:
  		update member
		set h_school_code = (select school_code 
		                     from h_school
		                     where school_name = #{highSchool}),
		    h_school_cate = #{hSchoolCate}
		where user_id = #{registerId}

  		update member
		set u_school_code = (select code 
		                     from u_school
		                     where name=#{university}),
		    u_school_cate = #{uSchoolCate}
		where user_id = #{registerId}

  		update member
		set user_name=#{userName}, pass=#{pass}, email=#{email}, email_domain=#{emailDomain},
			phone1=#{phone1}, phone2=#{phone2}, phone3=#{phone3},
		    zipcode=#{zipcode}, address=#{address}, address_detail= #{addressDetail}
		where user_id = #{userId}

update member
set withdraw = 'Y'
where user_id=#{id}
